import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Register from "./Register";
import Login from "./Login";

function App() {
    const [darkMode, setDarkMode] = useState(true);

    const toggleDarkMode = () => {
        setDarkMode((prevMode) => !prevMode);
    };

    return (
        <Router>
            <div style={{ ...styles.container, backgroundColor: darkMode ? "#0d1117" : "#f5f5f5" }}>
                <div style={{ ...styles.card, backgroundColor: darkMode ? "#161b22" : "#ffffff", color: darkMode ? "#c9d1d9" : "#333" }}>
                    <button onClick={toggleDarkMode} style={styles.toggleButton}>
                        <span style={styles.moon}>{darkMode ? "☽" : "☀"}</span>
                    </button>
                    <h2 style={styles.heading}>Sign in</h2>
                    <Routes>
                        <Route path="/register" element={<Register />} />
                        <Route path="/login" element={<Login />} />
                    </Routes>
                    <div style={styles.linkContainer}>
                        <Link to="/register" style={{ ...styles.link, color: darkMode ? "#58a6ff" : "#007bff" }}>
                            New here? Create an account
                        </Link>
                    </div>
                </div>
            </div>
        </Router>
    );
}

const styles = {
    container: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        transition: "background-color 0.3s ease",
    },
    card: {
        padding: "20px",
        borderRadius: "8px",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.2)",
        textAlign: "center",
        width: "300px",
        transition: "background-color 0.3s ease, color 0.3s ease",
    },
    heading: {
        marginBottom: "20px",
    },
    linkContainer: {
        marginTop: "10px",
    },
    link: {
        textDecoration: "none",
    },
    toggleButton: {
        border: "none",
        background: "none",
        cursor: "pointer",
        fontSize: "20px",
        position: "absolute",
        top: "15px",
        right: "15px",
    },
    moon: {
        display: "inline-block",
        fontSize: "24px",
    },
};

export default App;
