import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Landing = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const message = location.state?.message || "WELCOME"; // Default message if none provided

    // Redirect to login after 3 seconds if the user came from registration
    React.useEffect(() => {
        if (message === "REGISTER SUCCESS") {
            const timer = setTimeout(() => {
                navigate("/login");
            }, 3000);
            return () => clearTimeout(timer); // Cleanup timer on unmount
        }
    }, [message, navigate]);

    return (
        <div style={styles.container}>
            <h1 style={styles.successText}>{message}</h1>
        </div>
    );
};

const styles = {
    container: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        backgroundColor: "#f5f5f5", // Match App.js background
    },
    successText: {
        fontSize: "48px",
        fontWeight: "bold",
        color: "green",
        textAlign: "center",
    },
};

export default Landing;
